def extract_year(date_var):
    try:
        year = datetime.datetime.strptime(date_var, '%m/%d/%y').year
        if year >= 2018:
            year = re.sub('^20','19',str(year))
        return year
    except:
        return -1

def extract_month(date_var):
    try:
        return datetime.datetime.strptime(date_var, '%m/%d/%y').month
    except:
        return -1

def extract_day(date_var):
    try:
        return datetime.datetime.strptime(date_var, '%m/%d/%y').day
    except:
        return -1

